//用于计算最新的状态

import { ADD, DEL, CHANGE } from "./actionType";

//仓库一开始默认的数据
let defaultState = {
    list:[
        {
            content: "学习react",
            status: false,
        },
        {
            content: "扫地",
            status: false,
        },
        {
            content: "投简历",
            status: false,
        },
        {
            content: "背面试题",
            status: false,
        },
    ],
}





/**
 * 通过这个纯函数计算出最新的状态
 * state : 仓库数据
 * action : 描述对象 {type: ADD, text: "学习 redux"}
 */
export function todoReducer(state = defaultState, action) {
    //有了描述对象后，就可以根据就状态计算出新的状态并返回
    switch (action.type) {
        case ADD: {
            //新增的操作
            const arr = [...state.list];
            arr.push({
                content: action.data,
                state: false,
            })
            return { list: arr };
        };
        default: return state;
    }
}
