import React from 'react';

function Hello(props) {
  
    function clickHandle() {
        props.changeStateHandle();
    }
    return (
        <>
            <ul>
            <li>姓名：{props.stuInfo.name}</li>
                <li>年龄：{props.stuInfo.age}</li>
                <li>性别：{props.stuInfo.gender}</li>
                <li>content: {props.content}</li>
                <li>num: {props.num}</li>
                <button onClick={clickHandle}>触发父组件函数</button>
        </ul>
        </>
    );
}

//设置 props 的默认值
Hello.defaultProps = {
    content:100
}

export default Hello;
