import React from "react";

class App extends React.Component() {
  constructor(){
    super();
    this.state = {
      num:1
    }
    this.timer = setInterval(() => {
      this.setState({
        num:this.state.num++
      },1000)
    })
  }
  clickHandle = () => {
    this.setState({
      num: this.state.num + 1
    })
    console.log(this.state.num);
  }
  render(){
    console.log("render");
  }
}

export default App;
