      this.setState((cur) => ({
        num:cur.num+1
      }))
      this.setState((cur) => ({
        num:cur.num+1
        }))
  }
  render(){
    return (
      <>
        <div>{this.state.num}</div>
        <button onClick={this.clickHandle}>+1</button>
      </>
    );
  }
}

export default App;
