import React from "react";
import Money from "./component/Money";
class App extends React.Component {
  state = {
    dollar: "",
    rmb:""
  }
  render() {
    return (
      <div>
        <Money text="美元" money={this.state.dollar} />
        <Money text="人民币" money={this.state.rmb} />
      </div>
    )
  }
}

export default App;
