import React from "react";
import Hello from "./component/Hello";
import Say from "./component/Say";
import Button from "./component/Button";

class App extends React.Component {
  constructor(){
    super();
    // this.state = {
    //     num:1
    // }
    this.state = {
      name: "tt",
      age: 22,
      gender:"woman"
    }
}
  // state = {
  //   num:1
  // }
  clickHandle = () => {
    this.setState((cur) => ({
      num:cur.num+1
      }))
      this.setState((cur) => ({
        num:cur.num+1
      }))
      this.setState((cur) => ({
        num:cur.num+1
        }))
  }

  //该方法会通过 props 的形式传递给子组件
  changeStateHandle(num) {
    console.log("子组件传递的数据",num);
  }

  render(){
    return (
      <>
        <div>{this.state.num}</div>
        <button onClick={this.clickHandle}>+1</button>
        <Hello stuInfo={this.state} num={1} changeStateHandle={ this.changeStateHandle} />
        <Say content="crule summer" />
        <Button>添加按钮</Button>
      </>
    );
  }
}

export default App;
