import { useState,useEffect} from 'react';

function App(props) {

  let [count, setCount] = useState(0);

    // 声明多个 state 变量
    let [age, setAge] = useState(42);
    const [fruit, setFruit] = useState('banana');
   let [todos, setTodos] = useState([{ text: '学习 Hook' }]);

  function handleOrangeClick() {
    // 和 this.setState({ fruit: 'orange' }) 类似
    // setAge(++age);
    setFruit('orange');
    setTodos([{ text: '投简历找工作' }]);
  }
  return (
    <div>
      <div>年龄: {age}</div>
      <div>水果: {fruit}</div>
      <div>待办事项: {todos[0].text}</div>
      <button onClick={handleOrangeClick}>+1</button>
    </div>
  );
}

export default App;
