import React from "react";
import Hello from "./component/Hello";
import Say from "./component/Say"

class App extends React.Component {
  constructor(props){
    super(props);
    // this.state = {
    //     num:1
    // }
    this.state = {
      name: "tt",
      age: 22,
      gender:"woman"
    }
}
  // state = {
  //   num:1
  // }
  clickHandle = () => {
    this.setState((cur) => ({
      num:cur.num+1
      }))
      this.setState((cur) => ({
        num:cur.num+1
      }))
      this.setState((cur) => ({
        num:cur.num+1
        }))
  }
  render(){
    return (
      <>
        <div>{this.state.num}</div>
        <button onClick={this.clickHandle}>+1</button>
        <Hello stuInfo={this.state} />
        <Say stuInfo={this.state}/>
      </>
    );
  }
}

export default App;
